import axios from 'axios';
import React, {useState, useEffect } from 'react';
import './style.css';
import { TextField } from "@material-ui/core";


import Row from '../Row';

const GENERATION = {
    gen0 : 'https://pokeapi.co/api/v2/pokemon/?offset=0&limit=898',
    gen1 : 'https://pokeapi.co/api/v2/pokemon/?offset=0&limit=151',
    gen2 : 'https://pokeapi.co/api/v2/pokemon/?offset=151&limit=100',
    gen3 : 'https://pokeapi.co/api/v2/pokemon/?offset=251&limit=134',
    gen4 : 'https://pokeapi.co/api/v2/pokemon/?offset=386&limit=107',
    gen5 : 'https://pokeapi.co/api/v2/pokemon/?offset=493&limit=156',
    gen6 : 'https://pokeapi.co/api/v2/pokemon/?offset=649&limit=72',
    gen7 : 'https://pokeapi.co/api/v2/pokemon/?offset=721&limit=88',
    gen8 : 'https://pokeapi.co/api/v2/pokemon/?offset=809&limit=89'
};



export default function Home(){
    const [pokelist, setPokelist] = useState([]);
    const [filterText, setFilterText] = useState("");
    const [filterList, setFilterList] = useState([]);


    const [generation, setGeneration] = useState("gen1");

    const handleChange = (event) => {
      setFilterText(event.target.value);
    };

    function HandlerGen() {
        return (
          <div className="select"> 
            <select 
            value={generation}

              onChange={(e) => {
                const selectGeneration = e.target.value;  
                setGeneration(selectGeneration)
              }} 
            >
              <option value="gen0">All Generations</option>
              <option value="gen1">Generation I</option>
              <option value="gen2">Generation II</option>
              <option value="gen3">Generation III</option>
              <option value="gen4">Generation IV</option>
              <option value="gen5">Generation V</option>
              <option value="gen6">Generation VI</option>
              <option value="gen7">Generation VII</option>
              <option value="gen8">Generation VIII</option>
            </select>
          </div>
        );
      }



      useEffect(() => {
        async function getPokemon() {
            const res = await axios.get(GENERATION[generation]);
            setPokelist(res.data['results']);
            setFilterList(pokelist.filter(obj => obj.name === filterText));
            console.log(filterList.map(obj => obj.name))
        }
        getPokemon();

      },[]);



        return (
            <div>
               {HandlerGen()}
                        <TextField
                         onChange={handleChange}
                         label="Pokemon"
                         variant="standard"
                        />
                <div className="screen-border">

                    <div className="screen">

                            {pokelist ? (
                                <div className="row">

                                    {filterList.map(pokemon => (

                                        <Row 
                                        key={pokemon.name}
                                        name={pokemon.name}
                                        url={pokemon.url}
                                        />

                                    ))}
                                </div>
                            ) : (
                                    <p>Loading pokemon</p>
                                )}    
                    </div>
                </div>
            </div>

        );
    }
